mapper.select:
SELECT MEMBER_ID
FROM EV_MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.insert:
INSERT INTO EV_MEMBER(
			MEMBER_CODE
			, MEMBER_ID
			, MEMBER_PW
			, MEMBER_NAME
			, CAR_INFO
			, CAR_NUM
			, MEMBER_TEL
			, MEMBER_EMAIL
			, SALT
			) VALUES (
				(SELECT NVL(MAX(TO_NUMBER(MEMBER_CODE))+1, 1) FROM EV_MEMBER)
				, #{memberId}
				, #{memberPw}
				, #{memberName}
				, #{carInfo}
				, #{carNum}
				, #{memberTel}
				, #{memberEmail} 
				, #{salt}
			)
